void pt_bc_ply_join_not_handler::handler( const pt_bc_ply_join_not& noti,CUserSession* session )
{
	SERVER_LOG("pt_bc_ply_join_not_handler. join_guid:%lld, cur_guid: %lld ",noti.ply_data_.ply_guid_, session->ply_guid());

	session->players_.push_back(noti.ply_data_);
	SERVER_LOG("players_ size:%d", session->players_.size());
}
